// SPUD Additions
if (ANDROID_BUILD_VERSION >= 21)
{
	Log.debug("SPUD Setting NoBackupFilesDir: " + getNoBackupFilesDir().getPath());
	nativeSPUDSetNoBackupFilesDir(true, getNoBackupFilesDir().getPath());
}
else
{
	Log.debug("SPUD android version less than 21, defaulting NoBackupFilesDir: " + getFilesDir().getPath());
	nativeSPUDSetNoBackupFilesDir(false, getFilesDir().getPath());
}
// END SPUD Additions
